import { createBottomTabNavigator, createStackNavigator } from 'react-navigation';
import {
  View, Text,
  FlatList,
  // ListItem,
  StyleSheet,
  ActivityIndicator,  CheckBox,
  TouchableOpacity
} from 'react-native'

const list = [
  {
    title: 'Appointments',
    icon: 'av-timer'
  },
  {
    title: 'Trips',
    icon: 'flight-takeoff'
  },
]



const MainView = (props) => {
  // console.log('23- inside MainView ListItem=', ListItem.Content);
