  const { navigation } = props;

  const [isSelected, setSelection] = useState(false);

  return (<>

<View>

<CheckBox
          value={isSelected}
          onValueChange={setSelection}
          style={styles.checkbox}
        />


  <Button
    title="All Inbox (make it Flat List"
    type="outline"
    onPress={() => navigation.navigate('TabOneScreen')}
  />  
  <Badge value="99+" status="primary" />
  </View>

<Button
    title="Login "
    type="outline"
    onPress={() => navigation.navigate('LoginScreen')}
  />

<Button
    title="Red Priority "
    type="outline"
    onPress={() => navigation.navigate('HighPriority')}
  />

<Button
    title="Multi Selector "
    type="outline"
    onPress={() => navigation.navigate('MultiSelector')}
  />

{/* <Button
    title="Calendar Event"
    type="outline"
    onPress={() => navigation.navigate('CalendarEvent')}
  /> */}

<Button
    title="High Priority "
    type="outline"
    onPress={() => navigation.navigate('HighPriority')}
  />



<Button
    title="Followup "
    type="outline"
  />

<Button
    title="Call Back "
    type="outline"
  />


</>


  )
}

