import React from 'react';
import { View } from 'react-native';

// const CardSection = (props) => {
const PECardSection = (props) => {
  return (
// <View style={styles.containerStyle}>   if there is style being passed into this fun as a parameter, then styles on the right will override the left styles parameter
    <View style={[styles.containerStyle, props.style]}>
      {props.children}
    </View>
  );
};

const styles = {
  containerStyle: {
    borderBottomWidth: 1,
    padding: 5,
    backgroundColor: '#fff',
    justifyContent: 'flex-start',
    flexDirection: 'row',
    borderColor: '#ddd',
    position: 'relative'
  }
};

export { PECardSection };
