  constructor(props){
    super(props);
    this.state = {
      pageWidth: Dimensions.get('window').width,
      pageHeight: Dimensions.get('window').height,
      searchText: null,
      selected: [1,2]
    };
  }


  componentDidMount = () => {
    // const selected = this.props.selected
