const mapStateToProps = (state)  => {
  const { agent_name, address, _id, cell, phone, listPrice, Offer, agent_email_address, Bi_Weekly_P, FollowUp_Call_Date, comments } = state.propertyForm;

  return { agent_name, address, _id, cell, phone, listPrice, Offer, agent_email_address, Bi_Weekly_P, FollowUp_Call_Date, comments };
};


export default connect(mapStateToProps, { propertyUpdate, propertySave, propertyUpdatePriority })(MultiSelector);




